window.addEventListener("load",(()=>{document.getElementById("loader-bg").classList.add("hide");setTimeout((()=>{document.getElementById("loader-bg").style.display="none"}),400)}));document.addEventListener("DOMContentLoaded",(()=>{const sidebar=document.querySelector(".sidebar");const menuIcon=document.querySelector(".iconoMenu");const closeBtn=document.querySelector(".close-btn");menuIcon.addEventListener("click",(()=>sidebar.classList.add("active")));closeBtn.addEventListener("click",(()=>sidebar.classList.remove("active")));document.addEventListener("click",(e=>{if(!sidebar.contains(e.target)&&!menuIcon.contains(e.target)){sidebar.classList.remove("active")}}));gsap.registerPlugin(ScrollTrigger);gsap.from("header",{duration:1,y:-50,opacity:0,ease:"power2.out",scrollTrigger:{trigger:"header",start:"top top",toggleActions:"play none none none"}});gsap.from("footer .col-12",{duration:1,y:50,opacity:0,stagger:.3,ease:"power2.out",scrollTrigger:{trigger:"footer",start:"top 80%",end:"bottom 20%",toggleActions:"play none none reverse"}})}));