document.addEventListener("DOMContentLoaded",(()=>{const sidebar=document.querySelector(".sidebar");const menuIcon=document.querySelector(".iconoMenu");const closeBtn=document.querySelector(".close-btn");menuIcon.addEventListener("click",(()=>sidebar.classList.add("active")));closeBtn.addEventListener("click",(()=>sidebar.classList.remove("active")));document.addEventListener("click",(e=>{if(!sidebar.contains(e.target)&&!menuIcon.contains(e.target)){sidebar.classList.remove("active")}}));const carouselImages=document.querySelector(".carousel-images");const images=document.querySelectorAll(".carousel-images picture");const prevButton=document.querySelector(".carousel-control.prev");const nextButton=document.querySelector(".carousel-control.next");let currentIndex=0;let autoSlideInterval;function updateCarousel(){const offset=-currentIndex*100;carouselImages.style.transform=`translateX(${offset}%)`}function nextSlide(){currentIndex=(currentIndex+1)%images.length;updateCarousel()}function prevSlide(){currentIndex=(currentIndex-1+images.length)%images.length;updateCarousel()}function resetAutoSlide(){clearInterval(autoSlideInterval);autoSlideInterval=setInterval(nextSlide,7500)}if(images.length&&prevButton&&nextButton){updateCarousel();autoSlideInterval=setInterval(nextSlide,7500);nextButton.addEventListener("click",(()=>{nextSlide();resetAutoSlide()}));prevButton.addEventListener("click",(()=>{prevSlide();resetAutoSlide()}))}if(window.gsap&&window.ScrollTrigger){gsap.registerPlugin(ScrollTrigger);gsap.from(".carousel",{duration:1.5,y:-50,opacity:0,ease:"power2.out",scrollTrigger:{trigger:".carousel",start:"top 80%",toggleActions:"play none none reset"}});gsap.from(".carousel-images img",{duration:1,scale:.9,opacity:0,stagger:.3,ease:"power2.out",scrollTrigger:{trigger:".carousel",start:"top 80%",toggleActions:"play none none reset"}});gsap.from(".contenedorServicios",{duration:1,y:50,opacity:0,ease:"power2.out",scrollTrigger:{trigger:".contenedorServicios",start:"top 95%",toggleActions:"play none none reset"}});document.querySelectorAll(".contenedorServicios .col-12").forEach((col=>{gsap.from(col,{duration:1,y:50,opacity:0,ease:"power2.out",scrollTrigger:{trigger:col,start:"top 95%",toggleActions:"play none none reset"}})}));gsap.utils.toArray(".cuadro").forEach(((cuadro,i)=>{gsap.from(cuadro,{scrollTrigger:{trigger:cuadro,start:"top 85%",end:"bottom 15%",toggleActions:"play none none reverse"},opacity:0,y:50,duration:.6,delay:i*.1,ease:"power2.out"})}));gsap.from(".fotosPatrocinadores",{duration:1,y:50,opacity:0,ease:"power2.out",scrollTrigger:{trigger:".fotosPatrocinadores",start:"top 85%",toggleActions:"play none none reset"}});gsap.utils.toArray(".fotosPatrocinadores .imagenPatrocinador").forEach(((imagen,i)=>{gsap.from(imagen,{scrollTrigger:{trigger:".fotosPatrocinadores",start:"top 85%",toggleActions:"play none none reset"},opacity:0,y:30,duration:.6,delay:i*.2,ease:"power2.out"})}));gsap.from(".contenedorTextoUbicacion",{scrollTrigger:{trigger:".contenedorTextoUbicacion",start:"top 85%",toggleActions:"play none none reset"},opacity:0,y:50,duration:1,ease:"power2.out"});gsap.from(".iframe-mapa",{scrollTrigger:{trigger:".iframe-mapa",start:"top 85%",toggleActions:"play none none reset"},opacity:0,scale:.9,duration:1,ease:"power2.out"})}}));window.addEventListener("load",(()=>{document.getElementById("loader-bg").classList.add("hide");setTimeout((()=>{document.getElementById("loader-bg").style.display="none"}),400)}));