document.addEventListener("DOMContentLoaded",(()=>{if(typeof gsap!=="undefined"&&typeof ScrollTrigger!=="undefined"){gsap.registerPlugin(ScrollTrigger);gsap.from(".hero-section .container",{opacity:0,scale:.95,filter:"blur(10px)",duration:1.2,ease:"expo.out"});gsap.from(".about-section .container",{scrollTrigger:{trigger:".about-section",start:"top 85%",toggleActions:"play none none none"},opacity:0,x:120,duration:1.1,ease:"back.out(1.5)"});gsap.from(".contact-section .container",{scrollTrigger:{trigger:".contact-section",start:"top 85%",toggleActions:"play none none none"},opacity:0,y:80,scale:.95,duration:1,ease:"expo.out"});gsap.from(".accordion-item",{scrollTrigger:{trigger:".faq-section",start:"top 85%",toggleActions:"play none none none"},opacity:0,rotateX:60,y:40,duration:.7,stagger:.12,ease:"power3.out"});gsap.from(".faq-extra-box",{scrollTrigger:{trigger:".faq-extra-box",start:"top 90%",toggleActions:"play none none none"},opacity:0,x:-120,scale:.92,duration:.9,ease:"elastic.out(1, 0.7)"});gsap.from(".faq-extra-icon img",{scrollTrigger:{trigger:".faq-extra-box",start:"top 90%",toggleActions:"play none none none"},opacity:0,rotate:-180,duration:.7,delay:.2,ease:"power2.out"});gsap.from(".faq-extra-btn",{scrollTrigger:{trigger:".faq-extra-box",start:"top 90%",toggleActions:"play none none none"},opacity:0,y:40,duration:.5,delay:.35,ease:"back.out(2)"});gsap.utils.toArray(".services-section .col-lg-3 .col-md-12").forEach(((col,i)=>{gsap.fromTo(col,{opacity:0,y:-60,scale:.92},{scrollTrigger:{trigger:col,start:"top 92%",toggleActions:"play none none reverse"},opacity:1,y:0,scale:1,duration:.38,delay:i*.09,ease:"power2.out"})}))}const faqButtons=document.querySelectorAll(".accordion-button");faqButtons.forEach((button=>{button.addEventListener("click",(()=>{const expanded=button.getAttribute("aria-expanded")==="true";if(!expanded){gsap.to(button,{backgroundColor:"#e86e10",duration:.3})}else{gsap.to(button,{backgroundColor:"#fdb00a",duration:.3})}}))}))}));