document.addEventListener("DOMContentLoaded",(()=>{if(typeof gsap==="undefined"||typeof ScrollTrigger==="undefined")return;gsap.registerPlugin(ScrollTrigger);gsap.utils.toArray(".cuadro-servicio").forEach(((el,i)=>{gsap.from(el,{opacity:0,y:60,duration:.5,delay:i*.2,ease:"power2.out",scrollTrigger:{trigger:el,start:"top 85%",toggleActions:"play none none reverse"}})}));gsap.from(".servicios-header",{opacity:0,y:-40,duration:1,ease:"power2.out"});gsap.from(".servicios-contacto",{opacity:0,scale:.97,duration:.8,ease:"power2.out",scrollTrigger:{trigger:".servicios-contacto",start:"top 90%",end:"bottom 10%",toggleActions:"play none none reverse"}});gsap.from(".btn-hero",{opacity:0,y:20,duration:1,ease:"power2.out",delay:.5});gsap.from(".hero-title, .hero-subtitle",{opacity:0,y:-20,duration:1,ease:"power2.out",stagger:.2});gsap.from(".btn-solicitar",{opacity:0,scale:.9,duration:.8,ease:"power2.out",scrollTrigger:{trigger:".btn-solicitar",start:"top 90%",toggleActions:"play none none reverse"}})}));document.querySelector(".btn-hero").addEventListener("click",(e=>{e.preventDefault();const target=document.querySelector("#servicios-lista");const offset=window.innerWidth<=768?-50:-100;const targetPosition=target.getBoundingClientRect().top+window.scrollY+offset;gsap.to(window,{scrollTo:{y:targetPosition,autoKill:true},duration:1,ease:"power2.out"})}));